  try {
    const response = await axios.get(
      `https://api.openweathermap.org/data/2.5/forecast?q=${city}&lang=ru&units=metric&appid=${apiKey}`
    );
    return response.data;
  } catch (error) {
    if (error.response) {
      // обработка ошибок
    } else if (error.request) {
