import React, { useState, useEffect } from "react";
import { Card } from "../Card/Card";
import "./Card.css";
export const WeekContainer = () => {
  const [days, setDays] = useState([]);

  useEffect(() => {
    const weatherURL =
      "https://api.openweathermap.org/data/2.5/forecast?q=Kiev&lang=ru&units=metric&APPID=a9a3a62789de80865407c0452e9d1c27";

    fetch(weatherURL)
      .then((res) => res.json())
      .then((data) => {
        const dailyData = data.list.filter((reading) =>
          reading.dt_txt.includes("12:00:00")
        );
        setDays(dailyData);
      });
  }, []);

  const formatCards = () => {
    return days.map((day, index) => <Card day={day} key={index} />);
  };

  return (
    <div className="">
      <h1 className="display-4 jumbotron">Прогноз погоды на 5 дней</h1>
      <h5 className="display-4 text-muted">Kiev</h5>
      <div className="justify-content-center">{formatCards()}</div>
    </div>
  );
};
