import React, { useState, useEffect } from "react";
import { Card } from "../Card/Card";
import "./WeekContainer.css";
import { getWeatherData } from "../../api";

export const WeekContainer = () => {
  const [weather, setWeather] = useState({ current: null, forecast: [] });
  const [city, setCity] = useState("Ufa");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [current, forecast] = await Promise.all([
          getCurrentWeather(city),
          getWeatherData(city),
        ]);
        if (forecast && forecast.list) {
          const dailyData = forecast.list.filter((reading) =>
            reading.dt_txt.includes("12:00:00")
          );
          setWeather({ current, forecast: dailyData });
        } else {
          throw new Error("Failed to fetch weather data");
        }
      } catch (error) {
        console.error("Произошла ошибка при получении данных о погоде", error);
      }
    };
    fetchData();
  }, [city]);

  const formatCards = () => {
    return weather.forecast.map((day, index) => <Card day={day} key={index} />);
  };

  const handleCityChange = (event) => {
    setCity(event.target.value);
  };

  return (
    <div className="">
      <h1 className="display-4 jumbotron">Прогноз погоды на 5 дней</h1>
      <input type="text" value={city} onChange={handleCityChange} />
      <button onClick={() => console.log("Запрос на", city)}>
        Получить прогноз
      </button>
      <div className="justify-content-center">{formatCards()}</div>
      {/* Render the current weather here */}
      {weather.current && (
        <div>
          <h2>Current Weather</h2>
          {/* Display relevant current weather information here */}
          <div>Temperature: {weather.current.temperature}</div>
          {/* Add more current weather info as needed */}
        </div>
      )}
    </div>
  );
};
