import React, { useState, useEffect } from "react";
import { Card } from "../Card/Card";
import "./WeekContainer.css";
import { getWeatherData } from "../../api";

export const WeekContainer = () => {
  const [days, setDays] = useState([]);
  const [currentWeather, setCurrentWeather] = useState(null); // добавили состояние для текущей погоды
  const [city, setCity] = useState("Ufa");
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [weatherData, currentWeatherData] = await Promise.all([
          getWeatherData(city), // получаем прогноз на 5 дней
          getCurrentWeather(city), // получаем текущую погоду
        ]);

        if (weatherData && weatherData.list) {
          const dailyData = weatherData.list.filter((reading) =>
            reading.dt_txt.includes("12:00:00")
          );
          setDays(dailyData);
        } else {
          throw new Error("Failed to fetch weather data");
        }

        if (currentWeatherData) {
          setCurrentWeather(currentWeatherData); // устанавливаем текущую погоду в состояние
        }
      } catch (error) {
        console.error("Произошла ошибка при получении данных о погоде", error);
      }
    };
    fetchData();
  }, [city]);

  const formatCards = () => {
    return days.map((day, index) => <Card day={day} key={index} />);
  };

  const handleCityChange = (event) => {
    setCity(event.target.value);
  };

  return (
    <div className="">
      <h1 className="display-4 jumbotron">Прогноз погоды на 5 дней</h1>
      <input type="text" value={city} onChange={handleCityChange} />
      <button onClick={() => console.log("Запрос на", city)}>
        Получить прогноз
      </button>
      {currentWeather && ( // показываем текущую погоду, если она доступна
        <div>
          <h2>Текущая погода в городе {city}</h2>
          <p>Температура: {currentWeather.main.temp} °C</p>
          <p>Описание: {currentWeather.weather[0].description}</p>
        </div>
      )}
      <div className="justify-content-center">{formatCards()}</div>
    </div>
  );
};
